document.addEventListener("DOMContentLoaded",(()=>{setTimeout((()=>{document.body.classList.add("loaded")}),2500);const navbar=document.querySelector(".navbar");window.addEventListener("scroll",(()=>{if(window.scrollY>50){navbar.classList.add("scrolled")}else{navbar.classList.remove("scrolled")}}));document.querySelectorAll('a[href^="#"]').forEach((anchor=>{anchor.addEventListener("click",(function(e){e.preventDefault();const target=document.querySelector(this.getAttribute("href"));if(target){target.scrollIntoView({behavior:"smooth",block:"start"})}}))}));const observerOptions={root:null,rootMargin:"0px",threshold:.1};const appearOnScroll=new IntersectionObserver(((entries,observer)=>{entries.forEach((entry=>{if(!entry.isIntersecting)return;entry.target.classList.add("appear");observer.unobserve(entry.target)}))}),observerOptions);document.querySelectorAll("section, .step, .artwork-item, .glass-panel").forEach((item=>{item.classList.add("fade-in");appearOnScroll.observe(item)}));const steps=document.querySelectorAll(".step");steps.forEach(((step,index)=>{step.style.transitionDelay=`${index*.1}s`}));const artworks=document.querySelectorAll(".artwork-item");artworks.forEach(((artwork,index)=>{artwork.style.animationDelay=`${index*.05}s`}));const hero=document.querySelector(".hero");window.addEventListener("scroll",(()=>{const scrollPosition=window.scrollY;if(hero){hero.style.backgroundPositionY=`${scrollPosition*.4}px`}}))}));window.addEventListener("load",(()=>{document.body.classList.add("site-loaded");setTimeout((()=>{document.querySelectorAll(".fade-in").forEach((item=>{item.classList.add("appear")}))}),300)}));