document.addEventListener("DOMContentLoaded",function(){initScrollObserver();initSectionTitleAnimations();document.querySelectorAll(".step, .testimonial-wrapper, .glass-panel").forEach(element=>{element.classList.add("is-visible")})});function initScrollObserver(){const elements=document.querySelectorAll(".step, .testimonial-wrapper, .glass-panel");const observer=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add("is-visible");observer.unobserve(entry.target)}})},{threshold:.2,rootMargin:"0px 0px -50px 0px"});elements.forEach(element=>{observer.observe(element)})}function initSectionTitleAnimations(){const sectionTitles=document.querySelectorAll(".section-title");const titleObserver=new IntersectionObserver(entries=>{entries.forEach(entry=>{if(entry.isIntersecting){entry.target.classList.add("revealed");titleObserver.unobserve(entry.target)}})},{threshold:.2,rootMargin:"0px 0px -50px 0px"});sectionTitles.forEach(title=>{titleObserver.observe(title)})}